import statistics as stats
class P:
    def __init__(ad,cg,DaVe,R,C):
        ad.A=cg#Accept %chng of each data element
        ad.b=[]#An empty array which is to be filled with 6 elements from array A
        ad.D=DaVe#Accept indices array
        ad.stat=[]#Store coefficient of variation(C.V.) of array b as per condition
        ad.L=[]#Store working day of first element of array b as per condition
        ad.z=0
        ad.y=0
        ad.Rank=R#Store rank of country from corona affected list.
        ad.country=C
        ad.c=[]#Store stable indices 
    def adi(ad):
        
        for m in range(0,len(ad.A)-5,1):#Change the value of b by changing the first element of A every loop(Reference1)
            for n in range(0,6,1):#Feeding values in array b
                ad.b.append(ad.A[n])
            ad.switch=True
            for n in range(0,6,1):#Checking Condition for variation of %chng
                if ad.b[n]<=2 and ad.b[n]>=-2:
                    continue
                else:
                    ad.switch=False
                    break
            if ad.switch==True:#If the switch remains true after complete check;
                #Array of restricted(-2 to 2) %chng has been found
                ad.c.append(ad.D[m])
                ad.c.append(ad.D[m+1])
                ad.c.append(ad.D[m+2])
                ad.c.append(ad.D[m+3])
                ad.c.append(ad.D[m+4])
                ad.c.append(ad.D[m+5])
                ad.sta=(stats.stdev(ad.c)/stats.mean(ad.c))#Calculate C.V. of array
                ad.stat.append(ad.sta)#Store the calculated C.V. in an array mentioned above
                ad.L.append(m+1)#Store the working day of first element of b
                ad.count=1
                ad.o=min(ad.D)#Minimum index
                for n in ad.D:
                    if n==ad.o:
                        ad.y=ad.count#Working Day of Minimum index
                    else:
                        ad.count+=1
                
            else:
                ad.A.remove(ad.A[0])#Removing first element of A everytime(Connected to Reference1)
                ad.b=[]#Empty array b before feeing new values in
                ad.c=[]#Empty array c before feeing new values in

        print(ad.country,"=",ad.L) #Display the working day of all stable indeices from 2nd March       
        print("Number groups of stable indices formed = ",len(ad.L))
        ad.T=min(ad.stat)#Take least value from C.V.
        ad.count=0
        for n in ad.stat:
            if n==ad.T:
                ad.z=ad.count
                print(ad.z)
            else:
                ad.count+=1
        ad.m=ad.L[ad.z]#Get value of working day for least C.V.(To increase Accuracy)
        
        print("Working Days between lowest index and stable index = ",(ad.m+1)-ad.y)#Calculate Working Days between above m(stable index) and least index
    
        print("Stability arrives at % =",(ad.D[ad.m+1]-ad.D[ad.y])*100/ad.D[ad.y])
        #Calculate %chng between stable and lowest index point
        print("Most Destruction % =",-(min(ad.D)-max(ad.D))*100/max(ad.D))
        print("Factor for economic stability of country = ",(len(ad.L)*((ad.D[ad.m+1]-ad.D[ad.y])*100/ad.D[ad.m+1])*0.2)/(ad.Rank*((ad.m+1)-ad.y)))
        #Calculate answer of last question.
        print("")
